import { Component, OnInit } from '@angular/core';
import {config} from "rxjs";

@Component({
  selector: 'app-traffic-light',
  templateUrl: './traffic-light.component.html',
  styleUrls: ['./traffic-light.component.css']
})
export class TrafficLightComponent implements OnInit {

  config: any = {
    red: {
      duration: 4000,
      next: 'green',
    },
    yellow: {
      duration: 500,
      next: 'red',
    },
    green: {
      duration: 3000,
      next: 'yellow',
    },
  };

  stop = false;
  c: string = '';
  constructor() { }

  ngOnInit(): void {
    this.lights('red');
  }


  lights(color: string) {
    const {duration,next} = this.config[color];
    this.c = color;

    // console.log(this.config[color])
    // setInterval(()=>{this.lights(color.next)}, color.duration)
    // setTimeout(()=>{
    //
    //   if(!this.stop){
    //     this.lights(this.config.color.next)
    //   }
    // }, color.duration);
  }


}
<div id="trafficLightContainer">
  <div id="red" [ngClass]="{'red': c == 'red', 'green': c == 'green', 'yellow': c == 'yellow'}"></div>
  <div id="yellow"></div>
  <div id="green"></div>
</div>
